//(g)(c)(d),
int gcd(int a, int b)
{
    if(b == 0) return a;
    return gcd(b, a % b);
}
//num,len,rotdist
void rotate(int *nums, int len, int rotdist) {
    int i;
    for (i = 0; i < gcd(rotdist, len); i++) {
        int tmp = nums[i];
        int j = i;
        while (true) {
            int k = (j + rotdist) % len;
            if (k == i) break;
            nums[j] = nums[k];
            j = k;
        }
        nums[j] = tmp;
    }
}
