vector<string> anagram(vector<string>& Strs)
{
	unordered_map<string, vector<int>> map;
	vector<string> ans;
	int num = Strs.size();
	string tmp;
	for(int i = 0;i<num;++i)
	{
		tmp = Strs[i];
		sort(tmp.begin(), tmp.end());
		map[tmp].push_back(i);
	}
	for(auto &m : map)
	{
		int n = m.second.size();
		if(num > 1)
			for(int i = 0;i<n;++i)
				ans.push_back(Strs[m.second[i]]);
	}
	return ans;
}

