			if(a[i]>>bit) b[b_iterator ++] = a[i];
			else c[c_iterator ++] = a[i];
		}
		if(b_iterator <= c_iterator){
			ans += v;
			a = b;
			a_len = b_iterator;
		}else{
			a = c;
			a_len = c_iterator;
		}
	}
	return ans;
}
