#include<cstdio>
#include<algorithm>
using namespace std;
#define MAXN 1000
#define MAXC 100000

int weight[MAXN], value[MAXN], x[MAXN];
int dp[MAXN][MAXC];

int main(){
    freopen("*****/Test/data.in", "r", stdin);
    freopen("*****/Test/data.out", "w", stdout);
    int n, C;
    while(scanf("%d %d", &n, &C) != EOF){
        for(int i=0; i<n; ++i)    scanf("%d %d", &weight[i], &value[i]);       
        for(int i=0; i<=n; ++i){
            for(int j=0; j<=C; ++j){
                dp[i][j] = (i==0) ? 0 : dp[i-1][j];
                if(i>0 && j>=weight[i-1]) // i0n,ii-1
                    dp[i][j] = max(dp[i-1][j], dp[i-1][j-weight[i-1]]+value[i-1]);
            }
        }
        printf("%d\n", dp[n][C]);// answer

        int j=C; // backtracking
        for(int i=n; i>0; --i){
            if(dp[i][j] > dp[i-1][j]){
                x[i-1] = 1;
                j = j-weight[i-1];
            }
        }
        for(int i=0;i<n;++i) printf("%d ", x[i]);
        printf("\n");
    }
    fclose(stdin);
    fclose(stdout);
    return 0;
}
```



