#include<string>
#include<algorithm>
#include<iostream>
using namespace std;

int longestCommonSubsequence(string A, string B){
    int n = A.length();
    int m = B.length();
    int dp[n+1][m+1];
    for(int i = 1; i<=n; ++i)
        for(int j = 1; j<=m; ++j)
        {
            dp[i][j] = max(dp[i-1][j], dp[i][j-1]);
            if(A[i-1] == B[j-1])
                dp[i][j] = dp[i-1][j-1]+1;
        }
    return dp[n][m];
}

int main(){
    string A = "ABCD";
    string B = "DACB";
    cout << longestCommonSubsequence(A, B) << endl;
    return 0;
}
```

